mapper.update:
UPDATE FESTIVAL 
SET FESTIVAL_TITLE=#{festival_Title},FESTIVAL_CONTENT=#{festival_Content},
	FESTIVAL_Thumbnail=#{festival_Thumbnail}
	FESTIVAL_STARTDATE=#{festival_StartDate},FESTIVAL_ENDDATE=#{festival_EndDate},
	FESTIVAL_PHONE=#{festival_Phone},FESTIVAL_HOMEPAGE=#{festival_HomePage},
	FESTIVAL_ADDRESS=#{festival_Address},FESTIVAL_HOST=#{festival_Host},
	FESTIVAL_SUB=#{festival_Sub},FESTIVAL_PRICE=#{festival_Price},
	FESTIVAL_PROCEEDING=#{festival_Proceeding}
WHERE FESTIVAL_NO=#{festival_No}
